-- Table structure for sn_comment
-- ----------------------------
DROP TABLE IF EXISTS `sn_comment`;
CREATE TABLE `sn_comment`  (
  `comm_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '评论主键 自增',
  `comm_userid` int(11) NOT NULL COMMENT '外键 评论用户id',
  `comm_novelid` int(11) NOT NULL COMMENT '外键 评论小说id',
  `comm_content` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '评论内容 可以是富文本',
  `comm_time` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '评论时间,数据库自动生成',
