  `comm_parentId` int(11) NULL DEFAULT NULL COMMENT '评论的父评论id 用于评论回复',
  PRIMARY KEY (`comm_id`) USING BTREE,
  INDEX `fore_comm_userid`(`comm_userid`) USING BTREE,
  INDEX `fore_comm_novelid`(`comm_novelid`) USING BTREE,
  CONSTRAINT `fore_comm_novelid` FOREIGN KEY (`comm_novelid`) REFERENCES `sn_novel` (`novel_id`) ON DELETE CASCADE ON UPDATE RESTRICT,
  CONSTRAINT `fore_comm_userid` FOREIGN KEY (`comm_userid`) REFERENCES `sn_user` (`user_id`) ON DELETE CASCADE ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 56 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

