-- ----------------------------
-- Table structure for sn_novel
-- ----------------------------
DROP TABLE IF EXISTS `sn_novel`;
CREATE TABLE `sn_novel`  (
  `novel_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '小说主键id,唯一',
  `novel_title` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '小说名称',
  `novel_author` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '作者名称',
  `novel_categoryid` int(11) NOT NULL COMMENT '外键指向类别类型',
  `novel_updatetime` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '小说最后更新时间',
  `novel_isEnd` int(1) NULL DEFAULT 0 COMMENT '是否完结 0:未完结 1:已完结',
  `novel_summary` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '小说简介',
  `novel_share_userid` int(11) NOT NULL COMMENT '外键,小说分享者id,方便查询',
  `novel_downloadurl` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '下载地址,json对象\r\n{\'站内地址1\':\'url...\',\'站外地址1\':\'url..\'}',
  `novel_check` int(1) NULL DEFAULT 0 COMMENT '审核结果 0:为审核 1:通过 2:未通过',
  `novel_cover` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '小说封面',
  PRIMARY KEY (`novel_id`) USING BTREE,
  INDEX `fore_share_user`(`novel_share_userid`) USING BTREE,
  INDEX `fore_categoryid`(`novel_categoryid`) USING BTREE,
  INDEX `title`(`novel_title`) USING BTREE,
  INDEX `author`(`novel_author`) USING BTREE,
  INDEX `id`(`novel_id`) USING BTREE,
  CONSTRAINT `fore_categoryid` FOREIGN KEY (`novel_categoryid`) REFERENCES `sn_category` (`cat_id`) ON DELETE NO ACTION ON UPDATE CASCADE,
  CONSTRAINT `fore_share_user` FOREIGN KEY (`novel_share_userid`) REFERENCES `sn_user` (`user_id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB AUTO_INCREMENT = 80 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

